import React from "react";
import axios from "axios";

export const fetchArticles = cb => {
    axios
      .get('https://nc-news-c-matus.herokuapp.com/api/articles')
      .then(res => res.data)
      .then(allArticlesData => {
        cb(allArticlesData["articles"]);
      });
};
