import React from "react";
import axios from "axios";
const baseUrl = "https://nc-news-c-matus.herokuapp.com/api";

export const fetchArticles = cb => {
  axios
    .get(`${baseUrl}/articles`)
    .then(res => res.data)
    .then(data => {
      cb(data["articles"]);
    });
};

export const fetchUsers = cb => {
  axios
    .get(`${baseUrl}/users`)
    .then(res => res.data)
    .then(data => {
      cb(data["users"]);
    });
};

export const fetchTopics = cb => {
  axios
    .get(`${baseUrl}/users`)
    .then(res => res.data)
    .then(data => {
      cb(data["users"]);
    });
};
