import React from "react";
import styles from "./css/TopicCard.module.css";
import { Router, Link, navigate } from "@reach/router";
import { fetchArticles } from "./utils/getUtils";

class TopicCard extends React.Component {
  state = {
    articleCounts: null,
    isLoading: true
  };

  sneakyUpwardChange = articleCount => {
    this.setState({ articleCount, isLoading: false });
  };

  componentDidMount() {
    const articleCounts = {};
    this.props.topics.forEach(topic => {
      console.log(
        fetchArticles(
          x => {
            return x;
          },
          { topic: topic.slug }
        )
      );
      // articleCounts[topic.slug] =
    });
  }

  getArticleCount = topic => {
    return fetchArticles(this.sneakyUpwardChange, { topic }, true);
  };

  render() {
    return (
      <div className={styles.topicContainer}>
        {this.props.topics.map(topic => {
          return (
            <div className={styles.topicCard}>
              <h1 className={styles.slug}>{topic.slug}</h1>
              <p className={styles.description}>{topic.description}</p>
              <p className={styles.articleCount}>
                #
                {() => {
                  this.getArticleCount(topic.slug);
                }}{" "}
                articles
              </p>
            </div>
          );
        })}
      </div>
    );
  }
}

export default TopicCard;
