import React, { Component } from 'react';
import {voteOnArticle} from './utils/patchUtils'
import styles from "./css/ArticlePreview.module.css";

//The intention of all this is to check the actual number of votes an article has to display the user having voted new votecount.


class VoteDisplayOnArticle extends Component {

    state = {blank: null}

handleVote = () => {

    voteOnArticle(this.props.currentUser, this.props.article_id, 1);
     this.setState({})
}

    render() {
        return (
            <p className={styles.votes}>
         <span onClick={this.handleVote} role="img">⬆️</span>
  <p className={styles.voteCount}>{this.props.votes || 0}</p>
          <span onClick={()=>{voteOnArticle(this.props.currentUser, this.props.article_id, -1); this.setState({})}} role="img">⬇️</span>
            </p>
        );
    }
}

export default VoteDisplayOnArticle;
