import React from "react";
import ncnewslogo from "./logoncnews.png";
import styles from "./css/Navbar.module.css";
import { Router, Link, navigate } from "@reach/router";

class Navbar extends React.Component {
  logYourselfIn = () => {
    console.log("gonna log in");
    this.props.logInOrOut("jessjelly");
    console.log(this.props.currentUser);
  };

  logYourselfOut = () => {
    console.log("gonna log out");
    this.props.logInOrOut(null);
    console.log(this.props.currentUser);
  };

  componentDidUpdate(prevProps) {
    if (prevProps.currentUser !== this.props.currentUser) {
      console.log(66);
    }
  }

  render() {
    return (
      <div className={styles.navGrid}>
        {/* <div className={styles.leftContainer}> */}
        <Link to={"/"}>
          {" "}
          <img className={styles.ncLogo} src={ncnewslogo} alt="logo" />{" "}
        </Link>
        {/* </div> */}
        <div className={styles.searchbarContainer}>
          <input
            className={styles.searchBar}
            type="text"
            placeholder="Browse away!"
          />
        </div>
        <div className={styles.loginHolder}>
          {this.props.currentUser !== null &&
          this.props.currentUser !== undefined &&
          this.props.currentUser !== "" ? (
            <>
              <p className={styles.loggedInUser}>{this.props.currentUser}</p>
              <button
                onClick={this.logYourselfOut}
                className={styles.buttonRight}
              >
                Log out
              </button>
            </>
          ) : (
            <>
              <button
                className={styles.buttonRight}
                onClick={this.logYourselfIn}
              >
                Log In
              </button>
              <button className={styles.buttonRight}>Sign up</button>
            </>
          )}
        </div>
      </div>
    );
  }
}

export default Navbar;
