import React from "react";
import styles from "./css/SortTab.module.css";
import { Router, Link, navigate } from "@reach/router";

class SortTab extends React.Component {
  state = {
    currentlySelectedFilter: "created_at",
    sortDirection: "desc"
  };

  render() {
    return (
      <div className={styles.sortbar}>
        Sort by
        <p className={styles.button1}>date</p>
        <p className={styles.button2}>comment count</p>
        <p className={styles.button3}>votes</p>
      </div>
    );
  }
}

export default SortTab;
