import React from "react";
import styles from "./css/SortTab.module.css";
import { Router, Link, navigate } from "@reach/router";

class SortTab extends React.Component {
  constructor() {
    // CAN YOU REMOVE THIS COSTRCOTR LATER?
    super();

    this.state = {
      currentlySelectedFilter: "created_at",
      sortDirection: "desc",
      dropdownShowing: false
    };

    this.showDropdown = this.showDropdown.bind(this);
    this.closeDropdown = this.closeDropdown.bind(this);
  }

  showDropdown(event) {
    event.preventDefault();

    this.setState({ dropdownShowing: true }, () => {
      document.addEventListener("click", this.closeDropdown);
    });
  }

  closeDropdown() {
    this.setState({ dropdownShowing: false }, () => {
      document.removeEventListener("click", this.closeDropdown);
    });
  }

  render() {
    return (
      <div className={styles.sortbar}>
        <button onClick={this.showDropdown}>Sort by...</button>
        {this.state.dropdownShowing ? (
          <>
            <button className={`${styles.button1} ${styles.buttons}`}>
              date
            </button>
            <button className={`${styles.button2} ${styles.buttons}`}>
              comments
            </button>
            <button className={`${styles.button3} ${styles.buttons}`}>
              votes
            </button>
          </>
        ) : (
          ""
        )}
        <button className={`${styles.asc} ${styles.buttons}`}>^</button>
        <button className={`${styles.desc} ${styles.buttons}`}>v</button>
      </div>
    );
  }
}

export default SortTab;
