import React from "react";
import styles from "./css/SortTab.module.css";
import { Router, Link, navigate } from "@reach/router";

class SortTab extends React.Component {
  constructor() {
    super();

    state = {
      currentlySelectedFilter: "created_at",
      sortDirection: "desc",
      showDropdown: false
    };

    this.showMenu = this.showMenu.bind(this);
    this.closeMenu = this.closeMenu.bind(this);
  }

  render() {
    return (
      <div className={styles.sortbar}>
        Sort by
        <p className={`${styles.button1} ${styles.buttons}`}>date</p>
        <p className={`${styles.button2} ${styles.buttons}`}>comments</p>
        <p className={`${styles.button3} ${styles.buttons}`}>votes</p>
        <p className={`${styles.asc} ${styles.buttons}`}>^</p>
        <p className={`${styles.desc} ${styles.buttons}`}>v</p>
      </div>
    );
  }
}

class Card extends Component {
  constructor() {
    super();

    this.state = {
      showMenu: false
    };

    this.showMenu = this.showMenu.bind(this);
    this.closeMenu = this.closeMenu.bind(this);
  }

  showMenu(event) {
    event.preventDefault();

    this.setState({ showMenu: true }, () => {
      document.addEventListener("click", this.closeMenu);
    });
  }

  closeMenu() {
    this.setState({ showMenu: false }, () => {
      document.removeEventListener("click", this.closeMenu);
    });
  }

  render() {
    return (
      <div>
        <button onClick={this.showMenu}>Show menu</button>

        {this.state.showMenu ? (
          <div className="menu">
            <button> Menu item 1 </button>
            <button> Menu item 2 </button>
            <button> Menu item 3 </button>
          </div>
        ) : null}
      </div>
    );
  }
}

export default SortTab;
