import React from "react";
import styles from "./css/SortTab.module.css";
import { Router, Link, navigate } from "@reach/router";

class SortTab extends React.Component {
  constructor() {
    // CAN YOU REMOVE THIS COSTRCOTR LATER?
    super();

    this.state = {
      currentlySelectedFilter: "created_at",
      sortDirection: "desc",
      dropdownShowing: false
    };

    this.showDropdown = this.showDropdown.bind(this);
    this.closeDropdown = this.closeDropdown.bind(this);
  }

  showDropdown(event) {
    console.log(event.target.innerText);
    event.preventDefault();

    this.setState({ dropdownShowing: true }, () => {
      document.addEventListener("click", this.closeDropdown);
    });
  }

  closeDropdown(event) {
    if (
      event &&
      event.target &&
      event.target.innerText &&
      event.target.innerText === "Sort by"
    ) {
      return;
    } else {
      this.setState({ dropdownShowing: false }, () => {
        document.removeEventListener("mouseout", this.closeDropdown);
      });
    }
  }

  handleFilterClick = filter => {
    this.props.passUpQueries({ sort_by: filter });
    this.setState({ currentlySelectedFilter: filter });
    this.closeDropdown();
  };

  handleDirectionClick = direction => {
    this.props.passUpQueries({
      sort_by: this.state.currentlySelectedFilter,
      order: direction
    });
    this.setState({ sortDirection: direction });
  };

  render() {
    return (
      <>
        <div className={styles.fullWidthBar}>
          <div className={styles.leftContainer}>
            <div className={styles.sortbar}>
              <button className={styles.trigger} onClick={this.showDropdown}>
                Sort by
              </button>
              {this.state.dropdownShowing ? (
                <div className={styles.dropdown}>
                  <button
                    className={`${styles.button1} ${styles.dropButtons}`}
                    onClick={() => {
                      this.handleFilterClick("created_at");
                    }}
                  >
                    date
                  </button>

                  <button
                    className={`${styles.button2} ${styles.dropButtons}`}
                    onClick={() => {
                      this.handleFilterClick("comment_count");
                    }}
                  >
                    comments
                  </button>
                  <button
                    className={`${styles.button3} ${styles.dropButtons}`}
                    onClick={() => {
                      this.handleFilterClick("votes");
                    }}
                  >
                    votes
                  </button>
                </div>
              ) : (
                ""
              )}
              <p className={`${styles.asc} ${styles.littleButtons}`}>▲</p>
              <p className={`${styles.desc} ${styles.littleButtons}`}>▼</p>
            </div>
          </div>
          <div className={styles.rightContainer}>
            <Link to={"/users"}>
              <button className={styles.rightButton}>Users</button>
            </Link>
            <Link to={"/topics"}>
              <button className={styles.rightButton}>Topics</button>
            </Link>
            <Link to={"/write"}>
              <button className={styles.rightButton}>Write!</button>
            </Link>
          </div>
        </div>
      </>
    );
  }
}

export default SortTab;
