import React from "react";
import styles from "./css/SortTab.module.css";
import { Router, Link, navigate } from "@reach/router";

class SortTab extends React.Component {
  constructor() {
    // CAN YOU REMOVE THIS COSTRCOTR LATER?
    super();

    this.state = {
      currentlySelectedFilter: "created_at",
      sortDirection: "desc",
      showDropdown: false
    };

    this.showDropdown = this.showDropdown.bind(this);
    this.closeDropdown = this.closeDropdown.bind(this);
  }

  showDropdown(event) {
    event.preventDefault();

    this.setState({ showDropdown: true }, () => {
      document.addEventListener("click", this.closeDropdown);
    });
  }

  closeDropdown() {
    this.setState({ showDropdown: false }, () => {
      document.removeEventListener("click", this.closeDropdown);
    });
  }

  render() {
    return (
      <div className={styles.sortbar}>
        Sort by
        <p className={`${styles.button1} ${styles.buttons}`}>date</p>
        <p className={`${styles.button2} ${styles.buttons}`}>comments</p>
        <p className={`${styles.button3} ${styles.buttons}`}>votes</p>
        <p className={`${styles.asc} ${styles.buttons}`}>^</p>
        <p className={`${styles.desc} ${styles.buttons}`}>v</p>
      </div>
    );
  }
}

class Card extends Component {
  constructor() {
    super();

    this.state = {
      showDropdown: false
    };

    this.showDropdown = this.showDropdown.bind(this);
    this.closeDropdown = this.closeDropdown.bind(this);
  }

  showDropdown(event) {
    event.preventDefault();

    this.setState({ showDropdown: true }, () => {
      document.addEventListener("click", this.closeDropdown);
    });
  }

  closeDropdown() {
    this.setState({ showDropdown: false }, () => {
      document.removeEventListener("click", this.closeDropdown);
    });
  }

  render() {
    return (
      <div>
        <button onClick={this.showDropdown}>Show menu</button>

        {this.state.showDropdown ? (
          <div className="menu">
            <button> Menu item 1 </button>
            <button> Menu item 2 </button>
            <button> Menu item 3 </button>
          </div>
        ) : null}
      </div>
    );
  }
}

export default SortTab;
