import React, { Component } from "react";
import { voteOnArticle } from "./utils/patchUtils";
import styles from "./css/ArticlePreview.module.css";

//The intention of all this defunct is to check the actual number of votes an article has to display the user having voted new votecount.

class VoteDisplayOnArticle extends Component {
  state = { castedVote: 0 };

  handleVote = (currentUser, article_id, voteDirection) => {
    voteOnArticle(currentUser, article_id, voteDirection);
    this.setState({});
  };

  render() {
    return (
      <p className={styles.votes}>
        <span
          onClick={() => {
            voteOnArticle(this.props.currentUser, this.props.article_id, -1);
          }}
          role="img"
        >
          ^
        </span>
        <p className={styles.voteCount}>
          {this.props.votes + this.state.castedVote || 0}
        </p>
        <span
          onClick={() => {
            voteOnArticle(this.props.currentUser, this.props.article_id, -1);
            if (this.state.castedVote > 1) {
              this.state.castedVote--;
            }
          }}
          role="img"
        >
          v
        </span>
      </p>
    );
  }
}

export default VoteDisplayOnArticle;
