import React from "react";
import styles from "./css/TopicCard.module.css";
import { Router, Link, navigate } from "@reach/router";
import { fetchArticles } from "./utils/getUtils";

const TopicCard = props => {
  const getArticleCount = topic => {
    return fetchArticles(() => {}, { topic }, true);
  };

  return (
    <div className={styles.topicContainer}>
      {props.topics.map(topic => {
        return (
          <div className={styles.topicCard}>
            <h1 className={styles.slug}>{topic.slug}</h1>
            <p className={styles.description}>{topic.description}</p>
            <p className={styles.articleCount}>{getArticleCount(topic.slug)}</p>
          </div>
        );
      })}
    </div>
  );
};

export default TopicCard;
